INSERT INTO `products` (`bid`, `bname`, `decription`, `picture`, `create_by`, `detail`, `date_create`, `enable`, `cid`, `count_view`, `price`, `discount`) VALUES
(46, 'Samsung Galaxy Note 20 Ultra', '<h3>Sau&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-20\" target=\"_blank\">Galaxy Note 20</a>&nbsp;th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-20-ultra\" target=\"_blank\">Galaxy Note 20 Ultra</a>&nbsp;l&agrave; phi&ecirc;n bản tiếp theo&nbsp;cao cấp hơn thuộc d&ograve;ng Note 20 Series của Samsung, với nhiều thay đổi từ cụm camera hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/lay-net-laser-la-gi-756447\" target=\"_blank\">lấy n&eacute;t laser AF</a>&nbsp;c&ugrave;ng ống k&iacute;nh g&oacute;c rộng mới, m&agrave;n h&igrave;nh tr&agrave;n viền l&ecirc;n đến 6.9 inch chắc chắn sẽ l&agrave; chiếc điện thoại được săn đ&oacute;n của fan y&ecirc;u th&iacute;ch c&ocirc;ng nghệ.</h3>\r\n\r\n<h3>M&agrave;n h&igrave;nh 2K+&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-series-galaxy-s20-1245213\" target=\"_blank\">Dynamic AMOLED 2X</a>&nbsp;tr&agrave;n viền ho&agrave;n hảo, thiết kế thời thượng</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh tr&agrave;n viền g&oacute;c cạnh tối đa, kế thừa những đặc t&iacute;nh từ thế hệ trước, c&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;Dynamic AMOLED 2X cho gam m&agrave;u ch&iacute;nh x&aacute;c chuẩn điện ảnh cho bạn những trải nghiệm những thước phim ch&acirc;n thật tr&ecirc;n ch&iacute;nh&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">smartphone</a>&nbsp;của m&igrave;nh.</p>\r\n\r\n<p>Ngo&agrave;i ra, bằng c&aacute;ch giảm thiểu tối đa &aacute;nh s&aacute;ng xanh g&acirc;y hại, Dynamic AMOLED 2X c&ograve;n gi&uacute;p hạn chế t&igrave;nh trạng mỏi mắt gi&uacute;p tối ưu trải nghiệm của người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220522/samsung-galaxy-note-20-ultra3.jpg\" onclick=\"return false;\"><img alt=\"Màn hình tràn viền tối đa - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra3.jpg\" /></a></p>\r\n\r\n<p>Giới hạn khuyết điểm m&agrave;n h&igrave;nh ở mức tối đa cũng mang đến tỷ lệ hiển thị cao nhất, cao hơn so với những smartphone&nbsp;thiết kế tai thỏ hay giọt nước đ&atilde; qu&aacute; phổ biến tr&ecirc;n thị trường, m&aacute;y cũng sở hữu mặt k&iacute;nh cảm ứng Corning Gorilla Glass Victus&nbsp;bền bỉ, chắc chắn với khả năng giảm thiểu trầy xước cao.</p>\r\n\r\n<p>Mặt trước v&acirc;̃n l&agrave; camera selfie&nbsp;c&oacute; độ ph&acirc;n giải l&ecirc;n đến 10 MP đặt ở vị trí trung t&acirc;m, tuy nhi&ecirc;n n&ecirc;́u đ&ecirc;̉ ý kỹ bạn sẽ th&acirc;́y kích thước n&ocirc;́t ru&ocirc;̀i đã được t&ocirc;́i giản và tr&ocirc;ng nhỏ hơn so với phi&ecirc;n bản ti&ecirc;̀n nhi&ecirc;̣m.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220522/samsung-galaxy-note-20-ultra-254320-084302.jpg\" onclick=\"return false;\"><img alt=\"Camera selfie 10 MP - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra-254320-084302.jpg\" /></a></p>\r\n\r\n<h3>Hiệu năng mạnh mẽ với phần cứng được n&acirc;ng cấp</h3>\r\n\r\n<p>Tất nhi&ecirc;n một smartphone với thiết kế đỉnh cao th&igrave; sẽ kh&ocirc;ng thiếu đi sức mạnh phần cứng, Galaxy Note 20 Ultra sử dụng con chip Exynos 990 8 nh&acirc;n mạnh mẽ, đảm nhiệm gần như ho&agrave;n hảo mọi t&aacute;c vụ từ lướt web, xem phim, ph&aacute;t họa h&igrave;nh ảnh hay edit video độ ph&acirc;n giải cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220522/samsung-galaxy-note-20-ultra-235020-025011.jpg\" onclick=\"return false;\"><img alt=\"Trang bị chipset Exynos 990 | Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra-235020-025011.jpg\" /></a></p>\r\n\r\n<p>Với bộ nhớ RAM 8 GB, 256 GB ROM th&igrave; m&aacute;y c&oacute; thể thừa sức đa nhiệm tốt mọi t&aacute;c vụ v&agrave; lưu trữ số lượng lớn h&igrave;nh ảnh hay video, t&agrave;i liệu nặng, với điểm số hiệu năng AnTuTu cao ngất ngưỡng gần 500.000 điểm, Galaxy Note 20 Ultra ho&agrave;n to&agrave;n thoải m&aacute;i c&acirc;n mọi t&aacute;c vụ.</p>\r\n\r\n<p>Với cấu h&igrave;nh mạnh như vậy gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\">điện thoại chơi game</a>&nbsp;lu&ocirc;n lu&ocirc;n&nbsp;được&nbsp;ổn&nbsp;định với mức setting cao nhất,&nbsp;đảm bảo mọi trải nghiệm lu&ocirc;n&nbsp;được ho&agrave;n hảo.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220522/samsung-galaxy-note-20-ultra-253320-083337.jpg\" onclick=\"return false;\"><img alt=\"Điểm số AnTuTu cao ngất ngưỡng - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra-253320-083337.jpg\" /></a></p>\r\n\r\n<p>Cảm biến v&acirc;n tay si&ecirc;u &acirc;m&nbsp;dưới m&agrave;n h&igrave;nh được&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\">Samsung</a>&nbsp;trang bị tr&ecirc;n&nbsp;Galaxy Note 20 Ultra l&agrave; c&ocirc;ng nghệ cảm biến v&acirc;n tay hiện đại c&oacute; thể thao t&aacute;c mở kh&oacute;a nhanh ch&oacute;ng chỉ với 1 lần chạm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220522/samsung-galaxy-note-20-ultra5.jpg\" onclick=\"return false;\"><img alt=\"Thiết lập mật khẩu vân tay trên máy - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra5.jpg\" /></a></p>\r\n\r\n<h3>B&uacute;t S Pen mới, được n&acirc;ng cấp nhiều t&iacute;nh năng</h3>\r\n\r\n<p>B&uacute;t S Pen tr&ecirc;n Galaxy Note 20 Ultra c&oacute; sự thay đổi sang vị tr&iacute; b&ecirc;n tr&aacute;i thay v&igrave; b&ecirc;n phải như trước đ&acirc;y, độ trễ được giảm đi nhiều lần so với phi&ecirc;n bản tiền nhiệm, mang lại trải nghiệm mượt m&agrave; hơn. S Pen vẫn c&oacute; những t&iacute;nh năng như hỗ trợ cử chỉ th&ocirc;ng minh từ xa, chuyển chữ viết tay th&agrave;nh văn bản, chụp m&agrave;n h&igrave;nh nhanh,...</p>\r\n\r\n<p>Kh&ocirc;ng những c&oacute; t&iacute;nh năng hoạt động độc lập như tr&ecirc;n Galaxy Note 10, S Pen tr&ecirc;n Note 20 Ultra c&ograve;n được n&acirc;ng cấp để c&oacute; thể tương t&aacute;c thực tế ảo trực tiếp, điều khiển cử chỉ th&ocirc;ng minh hơn nhờ v&agrave;o loạt cảm biến ho&agrave;n to&agrave;n mới được Samsung &aacute;p dụng l&ecirc;n trong năm nay.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220522/samsung-galaxy-note-20-ultra6.jpg\" onclick=\"return false;\"><img alt=\"Cận cảnh bút S-Pen - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra6.jpg\" /></a></p>\r\n\r\n<h3>Camera độc đ&aacute;o, từ thiết kế đến t&iacute;nh năng</h3>\r\n\r\n<p>Note 20 Ultra với những n&acirc;ng cấp về cảm biến ảnh,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\">camera ch&iacute;nh</a>&nbsp;c&oacute; độ ph&acirc;n giải l&ecirc;n đến 108 MP bổ sung lấy n&eacute;t Laser AF, khắc phục những hạn chế trong lấy n&eacute;t ở những d&ograve;ng Note trước.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220522/samsung-galaxy-note-20-ultra1.jpg\" onclick=\"return false;\"><img alt=\"Camera chụp ảnh siêu sắc nét - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra1.jpg\" /></a></p>\r\n\r\n<p>Camera tele c&oacute; độ ph&acirc;n giải l&agrave; 12 MP với&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-zoom\" target=\"_blank\">khả năng zoom xa</a>&nbsp;tối đa l&ecirc;n đến 50x,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\">camera g&oacute;c si&ecirc;u rộng</a>&nbsp;c&oacute; độ ph&acirc;n giải l&agrave; 12 MP, đ&acirc;y l&agrave; một cảm biến cho khả năng chụp những bức ảnh phong cảnh nghệ thuật với g&oacute;c rộng hơn bao giờ hết.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra-225620-025648.jpg\" />Ảnh chụp g&oacute;c thường 1x tr&ecirc;n Galaxy Note 20 Ultra</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra-225620-025653.jpg\" />Ảnh chụp zoom 5x tr&ecirc;n Galaxy Note 20 Ultra</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra-225620-025658.jpg\" />Ảnh chụp zoom 10x tr&ecirc;n Galaxy Note 20 Ultra</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hơn thế nữa khi giờ đ&acirc;y bạn đ&atilde; c&oacute; thể c&oacute; những thước phim xuất sắc 8K nhờ c&ocirc;ng nghệ chống rung, lấy n&eacute;t tự động, quay si&ecirc;u chậm,... B&ecirc;n cạnh&nbsp;đ&oacute;, camera selfie 10 MP cho ra những bức&nbsp;ảnh&nbsp;đẹp lung linh, tự nhi&ecirc;n gi&uacute;p bạn chia sẽ l&ecirc;n c&aacute;c mạng x&atilde; hội hoặc chia sẽ c&ugrave;ng người th&acirc;n, bạn b&egrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra-233820-023855.jpg\" />Ảnh selfie tr&ecirc;n Galaxy Note 20 Ultra</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/220522/samsung-galaxy-note-20-ultra-233820-023844.jpg\" />Ảnh selfie tr&ecirc;n Galaxy Note 20 Ultra</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Pin dung lượng lớn, cho trải nghiệm cả ng&agrave;y d&agrave;i</h3>\r\n\r\n<p>Sở hữu vi&ecirc;n pin lớn 4500 mAh, Note 20 Ultra mang lại cho người d&ugrave;ng thời gian d&agrave;i sử dụng, b&ecirc;n cạnh đ&oacute; m&aacute;y c&ograve;n được hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\">c&ocirc;ng nghệ sạc nhanh</a>&nbsp;25w gi&uacute;p cho thời gian sạc pin được r&uacute;t ngắn đến mức tối thiểu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220522/samsung-galaxy-note-20-ultra-214720-114735.jpg\" onclick=\"return false;\"><img alt=\"Sở hữu pin lớn, hỗ trợ sạc nhanh - Samsung Galaxy Note 20 Ultra\" src=\"https://cdn.tgdd.vn/Products/Ima